import axios from 'axios'
const baseUrl = '/api/exercises'

const getAll  = ()  => {
    const request = axios.get(baseUrl)
    return request.then(response => response.data)
}

const getMostPopular = async () => {
    const response = await axios.get(`${baseUrl}/popular`)
    return response.data
}

const getTargetMuscles = async (muscleGroup = '') => {
    const url = `${baseUrl}/target-muscles${muscleGroup.length ? `/${muscleGroup}` : ''}`
    const response = await axios.get(url)
    return response.data
}

export default { getAll, getMostPopular, getTargetMuscles }
