import axios from 'axios'
import { useState, useEffect } from 'react'
import exerciseServices from './services/exercises'

const App = () => {
    const [exercises, setExercises] = useState([])

    useEffect(() => {
	exerciseServices
	    .getAll()
	    .then(exercises => {
		setExercises(exercises)
	    })
    }, [])

    const ExerciseToAddForm = () => {
	const addExercise = (event) => {
	    /*event.preventDefault()*/
	}
	return (
		<form onSubmit={addExercise}>		
		<div>
		    <select name="exercises" id="exercises">
	                {exercises.map(exercise => <option value={exercise.name}>{exercise.name}</option>)}
	            </select>
		</div>
		<div>
		    <label htmlFor="sets"> Sets </label>
		    <input id="sets" type="text" />
		</div>
		<div>
		    <label htmlFor="reps"> Reps </label>
		    <input id="reps" type="text" />
		</div>
	        </form>
	       )
    }
    return (
	    <div>
	        <p> Fitness Tracking App </p>
	        <ExerciseToAddForm />
	    </div>
    )

}

export default App
