import {
    BrowserRouter as Router,
    Routes, Route, Link, Navigate
} from 'react-router-dom'
import { useState, useEffect, useContext } from 'react'
import UserContext from './UserContext'

import exerciseServices from './services/exercises'
import workoutServices from './services/workouts'

import Home from './components/Home'
import RegistrationPage from './components/RegistrationPage'
import WorkoutHistoryPage from './components/WorkoutHistoryPage'
import Header from './components/Header'

import './index.css'

 // Error notification 
 const Notification = ({ message }) => {
	return message === null ? null : <div className='error'> {message} </div>
}

// Defines page where an individual user can log their workout
const UserPage = () => {
	const [user, userDispatch] = useContext(UserContext)
	const [errorMessage, setErrorMessage] = useState([])    	
	console.log(`current user ${JSON.stringify(user)}`)
	/* UserPage state variables and updater functions */
	const [exercises, setExercises] = useState([])
	const [workout, setWorkout] = useState({})
	const [currSets, setCurrSets] = useState(0)
	const [currReps, setCurrReps] = useState(0)
	const [openDropdown, setOpenDropdown] = useState(false)

	/* Gets and sets all of the exercises from the backend */
	useEffect(() => {
		exerciseServices
			.getAll()
			.then(exercises => {
				setExercises(exercises)
			})
	}, [])

	const handleSetChange = (event) => {	
		setCurrSets(event.target.value)
	}

	const handleRepChange = (event) => {
		setCurrReps(event.target.value)
	}

	const addCluster = (event) => {
		event.preventDefault()	    
		const [exercise, sets, reps] = [
		event.target.elements.exercises.value,
		event.target.elements.sets.value,
		event.target.elements.reps.value
		]
		const newCluster = {'exercise': exercise, 'sets': sets, 'reps': reps}

		/* Creates new workout with this cluster if first cluster of the workout */ 
		if (Object.keys(workout).length === 0) {
			workoutServices
				.create(newCluster)
				.then(createdWorkout => {			
					setWorkout({...createdWorkout})
					console.log(workout.clusters)
				})	    
		} else {
			/* Adds cluster to previously existing workout */		
			const updatedWorkout = {...workout, clusters: workout.clusters.concat(newCluster)}
			workoutServices
				.update(workout.id, updatedWorkout)
				.then(updatedWorkout => {			
					setWorkout(updatedWorkout)
				})
		}
	}

	const ClusterList = () => {
		console.log(`list clusters ${workout}`)	    
		if (Object.keys(workout).length === 0) {
			console.log('empty')
			return <label htmlFor="noClustersPrompt"> Add your first exercise for this workout! </label>
		} else {
			return (
				<table>
					<thead>
						<tr>
						<th> Exercise </th>
						<th> Sets </th>
						<th> Reps </th>
						</tr>
					</thead>
					<tbody>
						{workout.clusters.map(cluster =>
							<tr>
								<td> {cluster.exercise.name} </td>
								<td> {cluster.sets} </td>
								<td> {cluster.reps} </td>
							</tr>
						)}
					</tbody>
				</table>
			)
		}
	}

	  const [filteredExercises, setFilteredExercises] = useState([])
	  /*const [exercisesInput, setExerciseInput] = useState('')*/

	  const handleExerciseInputChange = (event) => {
		const userInputValue = event.target.value.toLowerCase()
		const filteredExercisesTemp = exercises.filter( exercise =>
			exercise.name.toLowerCase().startsWith(userInputValue)
		)
		setFilteredExercises(filteredExercisesTemp)
	  }

	return (
		<div className="user-main">
			<Header />					
			<div className="user-log">
				<Notification message={errorMessage}/>
				<div>
				<h2> Current Workout  </h2>	

				</div>
			<form onSubmit={addCluster}>		
			<div>
						<label htmlFor="exercise"> Exercise </label>
			<input list="exercise-list" id="exercises"  placeholder="Type to search..." onChange={handleExerciseInputChange}/>
			<datalist id="exercise-list">
			{filteredExercises.map( exercise => (
				<option key={exercise.id} value={exercise.name} />
			))}
			</datalist>
			</div>
			<div>
					<label htmlFor="sets"> Sets </label>
					<input id="sets" type="text" value={currSets} onChange={handleSetChange}/>
			</div>
			<div>
					<label htmlFor="reps"> Reps </label>
			<input id="reps" type="text" value={currReps} onChange={handleRepChange} />
			</div>
					<button type="submit"> Add </button>
			</form>
			<ClusterList />
			</div>	       
		</div>
	)	
}

const App = () => {
    // App state variables and updater functions
    const [user, userDispatch] = useContext(UserContext)
	// Gets logged-in user and token from localStorage if available */
	if (user === null) {
		const loggedUserJSON = window.localStorage.getItem('loggedFitnessAppUser');
		if (loggedUserJSON) {
		  const currentUser = JSON.parse(loggedUserJSON);
		  userDispatch({ type: 'LOG IN', user: currentUser });
		  workoutServices.setToken(currentUser.token);
		}
	}
    console.log(`app current user ${JSON.stringify(user)}`)
	console.log(user)

    return (
		<div>
			<Router>	        
				<Routes>
					<Route exact path="/user" element={
						user ? ( <UserPage /> ) : ( <Navigate replace to={"/"} /> )
					}
					/>
					<Route exact path="/register" element={
						user ? (
							<>
							<p>User state: {JSON.stringify(user)}</p>
							<Navigate replace to={"/user"} />
							</>
						) : (
							<RegistrationPage />
						)
						}
					/>
					<Route exact path="/workout-history" element={
						user ? (
							<>
							<p>User state: {JSON.stringify(user)}</p>
							<WorkoutHistoryPage />
							</>
						) : (
							<Navigate replace to={"/"} />
						)
						}
					/>
					<Route exact path="/" element={
						!user ? ( <Home /> ) : ( <Navigate replace to={"/user"} /> )
					}
					/> 
				</Routes>
			</Router>
		</div>
    )
}

export default App
