import axios from 'axios'
import { useState, useEffect } from 'react'
import exerciseServices from './services/exercises'
import workoutServices from './services/workouts'


import LoginForm from './components/LoginForm'

const App = () => {
    const [errorMessage, setErrorMessage] = useState([])

    const [exercises, setExercises] = useState([])
    const [workout, setWorkout] = useState({})

    const [currSets, setCurrSets] = useState(0)
    const [currReps, setCurrReps] = useState(0)

    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')
    const [user, setUser] = useState(null)

    useEffect(() => {
	exerciseServices
	    .getAll()
	    .then(exercises => {
		setExercises(exercises)
	    })
    }, [])

    const handleSetChange = (event) => {	
	setCurrSets(event.target.value)
    }

    const handleRepChange = (event) => {
	setCurrReps(event.target.value)
    }

    const addCluster = (event) => {
	event.preventDefault()
	const exercise = event.target.elements.exercises.value
	const sets = event.target.elements.sets.value
	const reps = event.target.elements.reps.value
	console.log(`exercise name ${exercise} sets ${sets} reps ${reps}`)
	const newCluster = {'exercise': exercise, 'sets': sets, 'reps': reps}

	if (Object.keys(workout).length === 0) {
	    console.log('new workout')
	    workoutServices
		.create(newCluster)
		.then(createdWorkout => {
		    console.log(createdWorkout)
		    setWorkout({...createdWorkout})
		    console.log(workout.clusters)
	 	})

	} else {
	    console.log('existing workout')
	    console.log(workout) 
	    const updatedWorkout = {...workout, clusters: workout.clusters.concat(newCluster)}
	    console.log(updatedWorkout)
	    workoutServices
		.update(workout._id, updatedWorkout)
		.then(updatedWorkout => {
		    setWorkout(updatedWorkout)
		})
	}
    }

    const listClusters = () => {
	console.log(`list clusters ${workout}`)
	if (Object.keys(workout).length === 0) {
	    return <label htmlFor="noClustersPrompt"> Add your first exercise for this workout! </label>
	} else {
	    return workout.clusters.map(cluster =>
		    <li>
		    <label htmlFor="exercise"> {cluster.exercise} </label>
		    <label htmlFor="sets"> {cluster.sets} </label>
		    <label htmlFor="reps"> {cluster.reps} </label>
		   </li>
	    )
	}
    }

    const Notification = ({message}) => {
	if (message === null) {
	    return null
	}

	return (
		<div className='error'>
		{message}
	    </div>
	)
    }

    return (
	    <div>
	    <h1> Fitness Tracking App </h1>
	    <Notification message={errorMessage}/>
	    <LoginForm username={username} setUsername={setUsername}
	    password={password} setPassword={setPassword}
	    user = {user} setUser={setUser}/>
	    <div>
	        <h2> Exercises Done Today </h2>
	    <ul>
	    {listClusters()}
	    </ul>
	    </div>
	    <form onSubmit={addCluster}>		
	        <div>
		    <select name="exercises" id="exercises">
	                {exercises.map(exercise => <option value={exercise.name}>{exercise.name}</option>)}
	            </select>		

		    <label htmlFor="sets"> Sets </label>
		    <input id="sets" type="text" value={currSets} onChange={handleSetChange}/>

		    <label htmlFor="reps"> Reps </label>
	            <input id="reps" type="text" value={currReps} onChange={handleRepChange} />
		    <button type="submit"> Add </button>
	        </div>
	    </form>	   
	    </div>
    )

}

export default App
