import { useState, useEffect } from 'react'
import { useUserValue } from '../UserContext'
import Chart from 'chart.js'
import workoutServices from '../services/workouts'

const UserProgressChart = ( {targetMuscles} ) => {
    const [userExercises, setUserExercises] = useState([])
    const [filteredUserExercises, setFilteredUserExercises] = useState([])
    const [exerciseClusters, setExerciseClusters] = useState([])

    useEffect(() => {
        const fetchUserWorkouts = async () => {
            const userExercisesTemp = await workoutServices.getUserWorkoutExercises()
            setUserExercises(userExercisesTemp)
            setFilteredUserExercises(userExercisesTemp)
        }
        fetchUserWorkouts()
    }, [])

    const handleExerciseInputChange = async (event) => {
        const userInputValue = event.target.value.toLowerCase()
        const filteredUserExercisesTemp = userExercises.filter( exercise => 
            exercise.toLowerCase().startsWith(userInputValue)
        )
        setFilteredUserExercises(filteredUserExercisesTemp)
    }
    //TODO: get users clusters that contain an exercise for selected muscle group (1 cluster per workout for simplicity)
    //also get the workout.createdAt parameter along with the cluster object (technically you just need the reps for this chart)
    //Then create chart using workout.createdAt (x-axis) and, for now, cluster.reps (y-axis)
    return (
        <div>
            <label htmlFor="">Exercise: </label>
            <input list="exercises-list" id="exercises" 
            placeholder='Choose an exercise...' onChange={handleExerciseInputChange}/>
            <datalist id="exercises-list">
                {filteredUserExercises.map( exercise => (
                    <option> {exercise} </option>
                ))}
            </datalist>
        </div>
    )
}

export default UserProgressChart
