import axios from 'axios'
const baseUrl = '/api/workouts'

let token = null

const setToken = newToken => {
    token = `Bearer ${newToken}`
}

const create = async newWorkout => {
    const config = {
	headers: { Authorization: token},
    }

    const response = await axios.post(baseUrl, newWorkout, config)
    return response.data
}

const update = async (id, newWorkout) => {
    const response = await axios.put(`${baseUrl}/${id}`, newWorkout)
    return response.data
}

export default { create, update, setToken }
